const ClassLabel = () => {
  const [isOpen, setIsOpen] = useState(false);
  const navigation = useNavigation();

  const toggleDropdown = () => {
    setIsOpen((prevState: any) => !prevState);
  };

  const navigateToSubject = (subject: string) => {
    navigation.navigate('Page1', {subject});
  };
  return (
    <View>
      <Text style={styles.text1}>Classes</Text>
      <View>
        <Pressable onPress={toggleDropdown}>
          <Text style={styles.text1}>{isOpen ? '^' : '>'}</Text>
        </Pressable>
        {isOpen && (
          <View>
            <Pressable onPress={() => navigateToSubject('Maths')}>
              <Text style={styles.text}>Maths</Text>
            </Pressable>
            <Pressable onPress={() => navigateToSubject('English')}>
              <Text style={styles.text}>English</Text>
            </Pressable>
            <Pressable onPress={() => navigateToSubject('Economics')}>
              <Text style={styles.text}>Economics</Text>
            </Pressable>
            <Pressable onPress={() => navigateToSubject('Accounts')}>
              <Text style={styles.text}>Accounts</Text>
            </Pressable>
          </View>
        )}
      </View>
    </View>
  );
};

