const HomeDrawer = () => {
  return (
    <Drawer.Navigator
      screenOptions={{
        drawerStyle: {
          backgroundColor: '#000000',
          width: 400,
        },
      }}>
      <Drawer.Screen
        name="Dashboard"
        component={DashboardScreen}
        options={({focused}) => ({
          drawerLabel: 'Dashboard',
          drawerLabelStyle: {
            color: focused ? '#ffffff' : '#c0c0c0',
            fontSize: 16,
          },
          drawerIcon: ({}) => <DashboardIcon />,
        })}
      />
      <Drawer.Screen
        name="Message"
        component={MessageScreen}
        options={({focused}) => ({
          drawerLabel: ({}) => <Messagecount />,
          drawerIcon: ({}) => <MessageIcon />,
        })}
      />
      <Drawer.Screen
        name="Chat"
        component={ChatScreen}
        options={({focused}) => ({
          drawerLabel: ({}) => <Chatcount />,
          drawerIcon: ({}) => <ChatIcon />,
        })}
      />
      <Drawer.Screen
        name="Challenges"
        component={ChallengesScreen}
        options={({focused}) => ({
          drawerLabel: 'Challenges',
          drawerLabelStyle: {
            color: focused ? '#ffffff' : '#c0c0c0',
            fontSize: 16,
          },
          drawerIcon: ({}) => <ChallengesIcon />,
        })}
      />
      <Drawer.Screen
        name="Classes"
        component={ClassLabel}
        options={({focused}) => ({
          drawerLabel: ({}) => <ClassLabel />,
          drawerIcon: ({}) => <ClassesIcon />,
        })}
      />
      <Drawer.Screen
        name="Timetable"
        component={TimetableScreen}
        options={({focused}) => ({
          drawerLabel: 'Timetable',
          drawerLabelStyle: {
            color: focused ? '#ffffff' : '#c0c0c0',
            fontSize: 16,
          },
          drawerIcon: ({}) => <TimetableIcon />,
        })}
      />
      <Drawer.Screen
        name="Notifications"
        component={NotificationsScreen}
        options={({focused}) => ({
          drawerLabel: ({}) => <Notificationcount />,
          drawerIcon: ({}) => <NotificationIcon />,
        })}
      />
    </Drawer.Navigator>
  );
};

